package com.example.springbacth.config;import com.example.springbacth.entity.Customer;import com.example.springbacth.repository.CustomerRepo;import org.springframework.batch.item.Chunk;import org.springframework.batch.item.ItemWriter;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Component;@Componentpublic class CustomerWriter implements ItemWriter<Customer> {    @Autowired    private CustomerRepo customerRepo;    @Override    public void write(Chunk<? extends Customer> chunk) throws Exception {        System.out.println("Thread name: "+Thread.currentThread().getName());        customerRepo.saveAll(chunk);    }}